function start(){for(var r=0;r<entry.rows;r++){entry.boxArr[r]=new Array;for(var e=0;e<entry.cols;e++){entry.boxArr[r][e]=r*entry.cols+e;var n=document.createElement("div");entry.boxArr[r][e]==randomS[0]?n.className="snake":entry.boxArr[r][e]==randomS[1]?n.className="food":n.className="block",n.id=r*entry.cols+e,n.style.top=20*r+"px",n.style.left=20*e+"px",entry.snakeBox.appendChild(n)}}}function randomSnake(){for(var r=[],e=0;e<2;){var n=Math.floor(20*Math.random()*30);r[e]=n,e++}return r}var entry={snakeBox:document.getElementById("snake-box"),rows:20,cols:30,boxArr:[]},randomS=randomSnake();start();